import axios from "axios";
import React, { useState } from "react";

function EditeazaConcert({ onClose }) {
  const [numarIdentificare, setNumarIdentificare] = useState("");
  const [artist, setArtist] = useState("");
  const [bilete, setBilete] = useState("");
  const [data, setData] = useState("");
  const [locatie, setLocatie] = useState("");

  const editConcert = async () => {
    try {
      // Perform the edit concert logic here
      console.log("Concert edited!");
    } catch (error) {
      console.error(error);
    }
  };
  const fetchConcertData = async () => {
    try {
      const response = await axios.get("http://localhost:3999/site/");
      setConcertData(response.data.concert);
    } catch (error) {
      console.error(error);
    }
  };
  const handleSubmit = (event) => {
    event.preventDefault();

    console.log("Form submitted!");
    console.log("Numar Identificare", numarIdentificare);
    console.log("Artist:", artist);
    console.log("Bilete:", bilete);
    console.log("Data:", data);
    console.log("Locatie:", locatie);

    editConcert();
    onClose(); // Call the onClose callback to close the modal
  };

  return (
    <div>
      <h2>Modifica Concertul</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="numarIdentificare">Numar identificare:</label>
        <input
          type="text"
          id="numarIdentificare"
          value={numarIdentificare}
          onChange={(event) => setNumarIdentificare(event.target.value)}
          required
        />
        <br />
        <label htmlFor="artist">Artist:</label>
        <input
          type="text"
          id="artist"
          value={artist}
          onChange={(event) => setArtist(event.target.value)}
          required
        />
        <br />

        <label htmlFor="bilete">Bilete:</label>
        <input
          type="number"
          id="bilete"
          value={bilete}
          onChange={(event) => setBilete(event.target.value)}
          required
        />
        <br />

        <label htmlFor="data">Data:</label>
        <input
          type="date"
          id="data"
          value={data}
          onChange={(event) => setData(event.target.value)}
          required
        />
        <br />

        <label htmlFor="locatie">Locatie:</label>
        <input
          type="text"
          id="locatie"
          value={locatie}
          onChange={(event) => setLocatie(event.target.value)}
          required
        />
        <br />

        <button type="submit">AdaugÄƒ</button>

        <button type="button" onClick={onClose}>
          Inchide fereastra
        </button>
      </form>
    </div>
  );
}

export default EditeazaConcert;
